using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;
using MongoDB.Bson;
using MongoDB.Driver;
using MongoDB.Shared;
using MongoDB.Bson.Serialization.Attributes;
using MongoDB.Bson.Serialization.Serializers;

namespace TG.Example
{
    public class MongoReplSet
    {
        /// <summary>
        /// InsertSingle
        /// </summary>
        /// <param name="name"></param>
        public void InsertSingle(string name)
        {
            IMongoDatabase db = GetDatabase();
            lkmoney my = new lkmoney()
            {
                name = name,
                creator = "sys",
                Metadata = new BsonDocument  
                {  
                   {"NO","lllllllllllllddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddddd"},  
                   {"Name",""}  
                }
            };

            db.GetCollection<lkmoney>("lkmoney").InsertOne(my);
        }

        private IMongoDatabase GetDatabase(string key = "")
        {
            var connectString = "mongodb://morton:10002/test";
            var mongoUrl = new MongoUrl(connectString);

            List<MongoServerAddress> address = new List<MongoServerAddress>();
            address.Add(new MongoServerAddress("morton", 10001));
            address.Add(new MongoServerAddress("morton", 10002));
            address.Add(new MongoServerAddress("morton", 10003));
            MongoClientSettings settings = new MongoClientSettings();
            settings.Servers = address;
            var client = new MongoClient(settings);
            return client.GetDatabase(mongoUrl.DatabaseName);
        }
    }
}
